import React from "react";
import { useRouter } from "next/router";

import Button from "../Common/Button/Button";

import styles from "./vacancies.module.scss";

export default function Vacancia({
    text = "",
    title = "",
    advantages = [],
    price = "",
    image = "",
    isResponse = false,
    id = 0,
}) {
    const router = useRouter();

    const onCardClick = (url) => {
        router.push(url);
    };
    return (
        <div className={styles.vacan} onClick={() => onCardClick(`vacancies/${id}`)}>
            <div className={styles.header}>
                <div>
                    {!(image.length === 0) ? (
                        <img alt="logo" className={styles.logo} src={image} />
                    ) : (
                        <svg
                            width="40"
                            height="40"
                            viewBox="0 0 40 40"
                            fill="none"
                            xmlns="http://www.w3.org/2000/svg"
                            xlinkHref="http://www.w3.org/1999/xlink"
                        >
                            <circle
                                cx="20"
                                cy="20"
                                r="20"
                                fill="url(#pattern0)"
                            />
                            <circle
                                cx="20"
                                cy="20"
                                r="19.5"
                                stroke="#AA139B"
                                strokeOpacity="0.15"
                            />
                            <defs>
                                <pattern
                                    id="pattern0"
                                    patternContentUnits="objectBoundingBox"
                                    width="1"
                                    height="1"
                                >
                                    <use
                                        xlinkHref="#image0_433_919"
                                        transform="translate(0 0.336134) scale(0.00840336)"
                                    />
                                </pattern>
                                <image
                                    id="image0_433_919"
                                    width="119"
                                    height="39"
                                    xlinkHref="data:image/png;base64,"
                                />
                            </defs>
                        </svg>
                    )}
                </div>
                <div>{price}</div>
            </div>
            <h3 className={styles.title}>{title}</h3>
            <div className={styles.elipses}>
                {advantages.map((item) => (
                    <div className={styles.elipse}>{item}</div>
                ))}
            </div>
            <div className={styles.desc}>{text}</div>
            <div className={styles.buttons}>
                {isResponse === true ? (
                    <>
                        <Button
                            className={`${styles.button} ${styles.response}`}
                            type="button"
                        >
                            Статус: на рассмотрении
                        </Button>
                        <Button className={`${styles.button}`} type="button">
                            Контакты
                        </Button>
                    </>
                ) : (
                    <>
                        <Button
                            className={`${styles.button} ${styles.answ}`}
                            type="button"
                        >
                            Откликнуться
                        </Button>
                        <Button
                            className={`${styles.button} ${styles.responded}`}
                            type="button"
                        >
                            Откликнулось: 0
                        </Button>
                    </>
                )}
            </div>
        </div>
    );
}
